/**
 * display item
 * Dies ist die Standardfunktion, mit der man detailierte Informationen 
 * zu einem bestimmten Inhalt vom Modul aufrufen kann.
 */
function Example_user_display($args)
{
    $tid = pnVarCleanFromInput('tid');
[...]
//Check der Zugriffsrechte
[...]

    $pnRender = new pnRender();

    // Sind die Inhalte gecacht?
    if ($pnRender->is_cached('example_user_display.htm', $tid)) {
       return $pnRender->fetch('example_user_display.htm', $tid);
    }

[...]
// weiterer Code
[...]
// Aufruf des Templates mit Cache-ID
    return $pnRender->Fetch('example_user_display.htm', $tid);
}

function Example_admin_update($args)
{
[...]
    if(pnModAPIFunc('Example',
                    'admin',
                    'update',
                    array('tid'    => $tid,
                          'name'   => $name,
                          'number' => $number))) {
        // Update Erfolgreich? Dann:
        pnSessionSetVar('statusmsg', pnVarPrepHTMLDisplay(_EXAMPLEUPDATED));

        // Übersicht & Seite im Cache löschen
        $pnRender =& new pnRender();
        $pnRender->clear_cache('example_user_row.htm', $tid);
        $pnRender->clear_cache('example_user_display.htm', $tid);
    }

    // Weiterleitung zu einer anderen Funktion
    pnRedirect(pnModURL('Example', 'admin', 'view'));

    // Return
    return true;
}
