DROP TABLE IF EXISTS nuke_counter;

CREATE TABLE nuke_counter (
 pn_type varchar(80) NOT NULL default '',
 pn_var varchar(80) NOT NULL default '',
 pn_count int(11) unsigned NOT NULL default '0'
) TYPE=MyISAM;

